package de.mb.util;import java.util.StringTokenizer;public class Person {	private String _firstName;	private String _lastName;	private StringList _middleNames;	public Person(String fullName) {		StringTokenizer st = new StringTokenizer(fullName, " ");		StringList sl = new StringList();		String nextElement;		while (st.hasMoreElements()) {			nextElement = st.nextToken();			sl.add(nextElement);		}		if (sl.size() < 2)			throw new NotEnoughNamesException("fullName must contains at least 2 token for firstName and lastName!");		setFirstName(sl.firstElement());		setLastName(sl.lastElement());		if (sl.size() > 2) {			_middleNames = new StringList();			for (int i = 1; i < sl.size() - 1; i++) {				nextElement = sl.get(i);				System.out.println(nextElement);				addMiddleName(nextElement);			}		}	}	public static void main(String[] args) {		Person p = new Person("   Marco     Thomas  Behnke    ");	}	public Person(String firstName, String lastName) {		this(firstName + " " + lastName);	}	public Person(String firstName, StringList middleNames, String lastName) {		this(firstName, lastName);		setMiddleNames(middleNames);	}	public void setLastName(String lastName) {		_lastName = lastName;	}	public void setFirstName(String firstName) {		_firstName = firstName;	}	public void setMiddleNames(StringList middleNames) {		_middleNames = middleNames;	}	public String getLastName() {		return _lastName;	}	public String getFirstName() {		return _firstName;	}	public String getMiddleName(int i) {		if (_middleNames == null)			return "";		if (i > countMiddleNames())			return "";		return _middleNames.get(i - 1);	}	public int countMiddleNames() {		return _middleNames.size();	}	public void addMiddleName(String name) {		if (_middleNames == null)			_middleNames = new StringList();		_middleNames.add(name);	}}