MLFactory.createResetButton("Eingaben löschen");        String allButtons = HTMLFactory.createParagraph(submit + " " + reset);        result =            HTMLFactory.createForm(                result + allButtons,                "name",                "frage_auswerten.php",                "post");        // put all in body-tag        result = HTMLFactory.createBody(result);        // add head-tag        result = questionaireHead + result;        // put all html-tag        result = HTMLFactory.createHtml(result);        return result.toString();    }    protected static String createHeader(IQuestionaire questionaire) {        String result = "";        /*String id = HTMLFactory.createTableCell(questionaire.getID(), "20%");        String name = HTMLFactory.createTableCell("<b>Fragebogen Titel</b>", "80%");        /*String datum =        	HTMLFactory.createTableCell(/*questionaire.getCreationDate(),", "10%");                result += HTMLFactory.createTableRow(id + name/* + datum);        result = HTMLFactory.createTable(result);*/        result += "Titel: " + questionaire.getTitle();        result = HTMLFactory.createHeader(result, 3);        result = HTMLFactory.createDiv(result, "head", HTMLConstants.ALIGN_CENTER);        return result;    }    protected static String createFooter(IQuestionaire questionaire) {        String result = "";        String author =            questionaire.getAuthor().getFirstName()                + " "                + questionaire.getAuthor().getLastName();        result += HTMLFactory.createTableCell("written by: " + author, "40%");        result = HTMLFactory.createTableRow(result);        result = HTMLFactory.createTable(result, 95, 0);        return result;    }}